# config.pyimport configimport addonHandleraddonHandler.initTranslation()CONFIG_SECTION = "xPlorer"CONFIG_SPEC = {    "autoSelectFirstItem": "boolean(default=True)",    "announceEmptyFolder": "boolean(default=True)",    "suppressDirectUIAnnounce": "boolean(default=True)",}def loadConfig():    # Create config if not exists    if CONFIG_SECTION not in config.conf:        config.conf[CONFIG_SECTION] = {}            # Set defaults for missing keys    conf = config.conf[CONFIG_SECTION]    for key, spec in CONFIG_SPEC.items():        if "default=" in spec and key not in conf:            default_value = spec.split("default=")[1].split(")")[0]            if default_value == "True":                conf[key] = True            elif default_value == "False":                conf[key] = False            else:                conf[key] = default_value            # Convert string values to boolean    result = {}    for key in CONFIG_SPEC:        if key in conf:            value = conf[key]            if isinstance(value, str):                if value.lower() in ("true", "1", "yes", "on"):                    result[key] = True                elif value.lower() in ("false", "0", "no", "off"):                    result[key] = False                else:                    result[key] = value            else:                result[key] = value        else:            result[key] = conf.get(key, None)                    return resultdef saveConfig(confDict):    # Update configuration    config.conf[CONFIG_SECTION] = confDict    config.conf.save()